import React, { useState, useEffect, useRef } from 'react';
import Navbar from './components/Navbar';
import Skills from './components/Skills';
import PersonalInfo from './components/PersonalInfo';
import './styles.css';

function App() {
  const [darkMode, setDarkMode] = useState(false);
  const welcomeRef = useRef(null);
  const skillsRef = useRef(null);
  const projectsRef = useRef(null);

  // Effect to load the saved mode from localStorage and apply it
  useEffect(() => {
    const savedMode = localStorage.getItem("displayMode") || "light";
    setDarkMode(savedMode === "dark");
    // Set the initial dark mode class based on saved mode
    if (savedMode === "dark") {
      document.documentElement.classList.add("dark");
    } else {
      document.documentElement.classList.remove("dark");
    }
  }, []);

  // Effect to save the dark mode state in localStorage whenever it changes
  useEffect(() => {
    if (darkMode) {
      localStorage.setItem("displayMode", "dark");
      document.documentElement.classList.add("dark");
    } else {
      localStorage.setItem("displayMode", "light");
      document.documentElement.classList.remove("dark");
    }
  }, [darkMode]);

  const scrollToSection = (ref) => {
    ref.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const toggleDarkMode = () => {
    setDarkMode(!darkMode); // Toggle dark mode state
  };

  return (
    <div>
      <Navbar
        scrollToSection={scrollToSection}
        welcomeRef={welcomeRef}
        skillsRef={skillsRef}
        projectsRef={projectsRef}
        toggleDarkMode={toggleDarkMode}
        darkMode={darkMode}
      />

      {/* Personal Info Section (Welcome Section) */}
      <section ref={welcomeRef}>
        <PersonalInfo />
      </section>

      {/* Skills Section */}
      <section ref={skillsRef}>
        <Skills />
      </section>

      {/* Projects Section */}
      <section ref={projectsRef} id="Projects">
        <h2>Projects</h2>
        {/* Add content here */}
      </section>

      {/* Info Section */}
      <section id="Info">
        <h2>More Info</h2>
        {/* Add content here */}
      </section>
    </div>
  );
}

export default App;
