import React, { useState, useEffect, useRef } from 'react';
import Navbar from './components/Navbar';
import Skills from './components/Skills';
import PersonalInfo from './components/PersonalInfo';
import './styles.css';

function App() {
  const [darkMode, setDarkMode] = useState(false);
  const welcomeRef = useRef(null);
  const skillsRef = useRef(null);
  const projectsRef = useRef(null);

  // Effect to add/remove dark mode class from HTML tag
  useEffect(() => {
    let savedMode = localStorage.getItem("displayMode")
    if (!savedMode) {
      savedMode = "light"
      setDarkMode(false)
      localStorage.setItem("displayMode", savedMode)
    }
    setDarkMode(savedMode == 'dark' ? true : false)
  }, [darkMode]); // Re-run when darkMode changes

  const scrollToSection = (ref) => {
    ref.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const toggleDarkMode = () => {
    setDarkMode(!darkMode); // Toggle dark mode state
  };

  return (
    <div>
      
      <Navbar
        scrollToSection={scrollToSection}
        welcomeRef={welcomeRef}
        skillsRef={skillsRef}
        projectsRef={projectsRef}
        toggleDarkMode={toggleDarkMode}
        darkMode={darkMode}
        
      />

      {/* Personal Info Section (Welcome Section) */}
      <section ref={welcomeRef}>
        <PersonalInfo />
        <div className={'${darkMode ? "Dark": ""}'}></div>
          <div className="flex flex-col gap-4 w-full h-screen place-content-left"></div>
            <div className="border rounded-;g p-4 mx-auto"></div>
            <button onClick={()=>{}} className="gb-gray-700 text-gray-100 border font-bold p-3 rounded-lg">{darkMode ? "Light" : "Dark"} </button>
      </section>

      {/* Skills Section */}
      <section ref={skillsRef}>
        <Skills />
      </section>

      {/* Projects Section */}
      <section ref={projectsRef} id="Projects">
        <h2>Projects</h2>
        {/* Add content here */}
      </section>

      {/* Info Section */}
      <section id="Info">
        <h2>More Info</h2>
        {/* Add content here */}
      </section>
    </div>
  );
}

export default App;
